mapper.update:
UPDATE tb_product
product_name=#{productName}, product_desc=#{productDesc}, img_addr=#{imgAddr}, normal_price=#{normalPrice}, promotion_price=#{promotionPrice}, priority=#{priority}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus}, point=#{point},
product_category_id=#{productCategory.productCategoryId}
WHERE product_id = #{productId} AND shop_id = #{shop.shopId}

mapper.select:
SELECT
product_id,product_name,product_desc,img_addr,normal_price,promotion_price,
priority,create_time,last_edit_time,point,enable_status,product_category_id,shop_id
FROM tb_product


    and shop_id=#{productCondition.shop.shopId}


        and product_category_id=#{productCondition.productCategory.productCategoryId}



        and product_name like '%${productCondition.productName}%'


        and enable_status = #{productCondition.enableStatus}


ORDER BY
priority DESC
LIMIT #{rowIndex},#{pageSize}

SELECT count(1) FROM tb_product


        and shop_id=#{productCondition.shop.shopId}


        and product_category_id=#{productCondition.productCategory.productCategoryId}



        and product_name like '%${productCondition.productName}%'


        and enable_status = #{productCondition.enableStatus}
